import java.util.*;public class PN {public static Set<String> O=new HashSet<String>();public static String evaluator(String i){Scanner t=new Scanner(i);String o=t.next();if(!O.contains(o)){return i;}int p=2;int n=0;int m=0;while(n!=m-1&&t.hasNext()){String c=t.next();if(O.contains(c)){n++;}else{m++;}p+=c.length()+1;}String a=evaluator(i.substring(2,p-1));String b=evaluator(i.substring(p));int v;int w;try{v=Integer.parseInt(a);w=Integer.parseInt(b);switch(o){case "+":return v+w+"";case "*":return v*w+"";case "-":return v-w+"";}}catch(Exception e){}return o+" "+a+" "+b;}public static void main(String[] args){O.add("+");O.add("*");O.add("-");Scanner c=new Scanner(System.in);for(int i=1;c.hasNext();i++){System.out.println("Case "+i+": "+evaluator(c.nextLine()));}}}